contracts/iebs_Faillapop_DAO.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.20;

import "./interfaces/IFP_Shop.sol";
import "./interfaces/IFP_NFT.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";


/** 
    @title FaillaPop voting DAO for disputes
    @author Faillapop team :D 
    @notice The contract allows to vote with FPT tokens on open disputes. If the dispute is resolved in favor of the buyer,
    the seller have to refund the buyer. If the dispute is resolved in favor of the seller, the sale is closed.
    @dev Security review is pending... should we deploy this?
    @custom:ctf This contract is of JC's mock-audit exercise at https://github.com/jcr-security/solidity-security-teaching-resources
*/
contract FP_DAO {

    /************************************** Constants *******************************************************/
    ///@dev The threshold for the random number generator
    uint constant THRESHOLD = 10;
    ///@dev The default number of voters for passing a vote
    uint constant DEFAULT_QUORUM = 100;


    /************************************** State vars and Structs *******************************************************/

    /** 
        @notice A Dispute includes the itemId, the reasoning of the buyer and the seller on the claim,
        and the number of votes for and against the dispute.
        @dev A Dispute is always written from the POV of the buyer
            - FOR is in favor of the buyer claim
            - AGAINST is in favor of the seller claim
     */
    struct Dispute {
        uint itemId;
        string buyerReasoning;
        string sellerReasoning;
        uint votesFor;
        uint votesAgainst;
        uint totalVoters;
    }

    ///@notice Current disputes, indexed by disputeId
    mapping(uint256 => Dispute) public disputes;
    ///@notice The ID of the next dispute to be created
    uint256 public nextDisputeId;
    ///@dev Mapping between user address and disputeId to record the vote. 1 is FOR, 2 is AGAINST
    mapping(address => mapping(uint => uint)) public hasVoted;
    ///@dev Mapping between disputeId and the result of the dispute. 1 is FOR, 2 is AGAINST
    mapping(uint256 => uint8) public disputeResult;
    ///@notice Password to access key features
    string private password;
    ///@dev The address of the Shop contract
    address public shop_addr;
    ///@dev The Shop contract
    IFP_Shop public shopContract;
    ///@dev The NFT contract
    IFP_NFT public nftContract;
    ///@dev The FPT token contract
    IERC20 public fptContract;
    ///@notice Min number of people to pass a vote
    uint256 quorum;


    /************************************** Events and modifiers *******************************************************/

    ///@dev Emitted when the contract configuration is changed, contains the address of the Shop
    event NewConfig(address shop, address nft);
    ///@dev Emitted when a user votes, contains the disputeId and the user address
    event Vote(uint disputeId, address user);
    ///@dev Emitted when a new dispute is created, contains the disputeId and the itemId
    event NewDispute(uint disputeId, uint itemId);
    ///@dev Emitted when a dispute is closed, contains the disputeId and the itemId
    event EndDispute(uint disputeId, uint itemId);
    ///@dev Emitted when a user is awarder a cool NFT, contains the user address
    event AwardNFT(address user);


    /**
        @notice Check if the caller is authorized to access key features
        @param magicWord The password to access key features
     */
    modifier isAuthorized(string calldata magicWord) {
        require(
            keccak256(abi.encodePacked(magicWord)) == keccak256(abi.encodePacked(password)),
            "Unauthorized");
        _;
    }


    ///@notice Check if the caller is the Shop contract
    modifier onlyShop() {
		require(
            msg.sender == shop_addr,
            "Unauthorized"
        );
        _;
    }


    /************************************** External *******************************************************/ 

    /**
        @notice Constructor to set the password
        @param magicWord The password to access key features
        @param shop The address of the Shop contract
        @param nft_addr The address of the NFT contract
        @param fpt_addr The address of the FPT token
     */
    constructor(string memory magicWord, address shop, address nft_addr, address fpt_addr) {
        password = magicWord;
        shop_addr = shop;
        shopContract = IFP_Shop(shop_addr);
        nftContract = IFP_NFT(nft_addr);
        fptContract = IERC20(fpt_addr);


    }

    /**
        @notice Update the contract's configuration details
        @param magicWord to authenticate as privileged user
        @param newMagicWord The new password to access key features
     */
    function updateConfig(
        string calldata magicWord, 
        string calldata newMagicWord, 
        address newShop,
        address newNft
    ) external isAuthorized(magicWord) {
        password = newMagicWord;
        shop_addr = newShop;

        shopContract = IFP_Shop(shop_addr);
        nftContract = IFP_NFT(newNft);

        emit NewConfig(shop_addr, newNft);
    }

    /**
        @notice Cast a vote on a dispute
        @param disputeId The ID of the target dispute
        @param vote The vote, true for FOR, false for AGAINST
     */
    function castVote(uint disputeId, bool vote) external { 
        require(hasVoted[msg.sender][disputeId] == 0, "You have already voted");

        uint votingPower = calcVotingPower(msg.sender);

        if (vote) {
            disputes[disputeId].votesFor += votingPower;
            hasVoted[msg.sender][disputeId] = 1;
        } else {
            disputes[disputeId].votesAgainst += votingPower;

        }      

        disputes[disputeId].totalVoters += 1;

        emit Vote(disputeId, msg.sender);
    }

    /**
        @notice Open a dispute
        @param itemId The ID of the item involved in the dispute
        @param buyerReasoning The reasoning of the buyer in favor of the claim
        @param sellerReasoning The reasoning of the seller against the claim
     */
    function newDispute( 
        uint itemId, 
        string calldata buyerReasoning, 
        string calldata sellerReasoning
    ) external onlyShop() returns (uint) {  

        uint dId = nextDisputeId;
        nextDisputeId += 1;

        disputes[dId] = Dispute(
            itemId,
            buyerReasoning,
            sellerReasoning,
            0,
            0,
            0
        );  

        emit NewDispute(dId, itemId);
        return dId;
    }    

    /**
        @notice Resolve a dispute if enough users have voted and remove it from the storage
        @param disputeId The ID of the target dispute
     */
    function endDispute(uint disputeId) external {  
        if (disputes[disputeId].totalVoters < quorum) {
            revert("Not enough voters");
        }

        uint itemId = disputes[disputeId].itemId;

        if (disputes[disputeId].votesFor > disputes[disputeId].votesAgainst) {
            buyerWins(itemId);
            disputeResult[disputeId] = 1;
        } else {
            sellerWins(itemId);
            disputeResult[disputeId] = 2;
        }

        delete disputes[disputeId];

        emit EndDispute(disputeId, itemId);
    }   


    /**
        @notice Cancel an ongoing dispute. Either by the buyer or blacklisting (shop contract)
        @param disputeId The ID of the target dispute
     */
    function cancelDispute(uint disputeId) external onlyShop() { 
        uint itemId = disputes[disputeId].itemId;     

        delete disputes[disputeId];

        emit EndDispute(disputeId, itemId);
    }   


    /**
        @notice Randomly award an NFT to a user if they voten for the winning side
        @param disputeID The ID of the target dispute
     */
    function checkLottery(uint disputeID) external { 
        require(hasVoted[msg.sender][disputeID] != 0, "User didn't vote");

        if(disputeResult[disputeID] == hasVoted[msg.sender][disputeID]) {
            lotteryNFT(msg.sender);
        } else {
            revert("User voted for the wrong side");
        }

    }      

    /************************************** Internal *******************************************************/

    /**
        @notice Run a PRNG to award NFT to a user
        @param user The address of the elegible user
     */
    function lotteryNFT(address user) internal { 
        uint randomNumber = uint8( 
            uint256(
                keccak256(
                    abi.encodePacked(
                        blockhash(block.number - 1), 
                        block.timestamp, 
                        user
        ))));

        if (randomNumber < THRESHOLD) {
            nftContract.mintCoolNFT(user);

        }

        emit AwardNFT(user);  
    }

    /**
        @notice Resolve a dispute in favor of the buyer triggering the Shop's return item and refund logic
        @param itemId The ID of the item involved in the dispute
     */
    function buyerWins(uint itemId) internal {
        shopContract.returnItem(itemId);
    }

    /**
        @notice Resolve a dispute in favor of the seller triggering the Shop's close sale dispute logic
        @param itemId The ID of the item involved in the dispute
     */
    function sellerWins(uint itemId) internal {
        shopContract.endDispute(itemId);
    }

    /**
        @notice Calculate the voting power of a user
     */
    function calcVotingPower(address user) internal returns (uint256) {
        return fptContract.balanceOf(user);
    } 

    /************************************** Views *******************************************************/

    /**
        @notice Query the details of a dispute
        @param disputeId The ID of the target dispute
     */
	function query_dispute(uint disputeId) public view returns (Dispute memory) {
		return disputes[disputeId];
	}


}

contracts/iebs_Faillapop_ERC20.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.20;

/*
* Template ERC20 token for governance
*/

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract FP_Token is ERC20, ERC20Burnable, Pausable, AccessControl {
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor() ERC20("FaillaPop Token", "FPT") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _mint(msg.sender, 1000000 * 10 ** decimals());
        _grantRole(MINTER_ROLE, msg.sender);
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        whenNotPaused
        // override
    {
        // super._beforeTokenTransfer(from, to, amount);
         _beforeTokenTransfer(from, to, amount);
    }
}

contracts/iebs_Faillapop_shop.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./interfaces/IFP_DAO.sol";
import "./interfaces/IFP_Vault.sol";

/** 
    @title The FaillaPop Shop!
    @author Faillapop team :D 
    @notice The contract allows anyone to sell and buy goods. The seller has to lock funds to avoid malicious behaviour.
        In addition, unhappy buyers can open a claim and the DAO will decide if the seller misbehaved or not.
    @dev Security review is pending... should we deploy this?
    @custom:ctf This contract is part of JC's mock-audit exercise at https://github.com/jcr-security/solidity-security-teaching-resources
*/
contract FP_Shop is AccessControl {
    /************************************** Constants *******************************************************/
    ///@dev The admin role ID for the AccessControl contract
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    ///@dev The DAO role ID for the AccessControl contract
    bytes32 public constant DAO_ROLE = keccak256("DAO_ROLE");
    ///@dev The blacklisted role ID for the AccessControl contract
    bytes32 public constant BLACKLISTED_ROLE = keccak256("BLACKLISTED_ROLE");


    /************************************** State vars  and Structs *******************************************************/
    /**
        @dev A Sale can be in one of three states: 
        `Selling` deal still active
        `Disputed` the buyer submitted a claim
        `Pending` waiting buyer confirmation
        `Sold` deal is over, no claim was submitted
        `Vacation` the seller is on vacation, sale halted
    */
    enum State {
        Selling,
        Pending,
        Disputed,
        Sold,
        Vacation
    }


    /**
        @dev A Sale struct represent each of the active sales in the shop.
        @param seller The address of the seller
        @param buyer The address of the buyer, if any
        @param title The title of the item being sold
        @param description A description of the item being sold
        @param price The price in Ether of the item being sold
        @param state The current state of the sale
     */
    struct Sale {
        address seller;
        address buyer;
        string title;
        string description; 
        uint price;
        State state;
    }  


    /**
        @dev A Dispute struct represent each of the active disputes in the shop.
        @param itemId The ID of the item being disputed
        @param buyerReasoning The reasoning of the buyer for the claim
        @param sellerReasoning The reasoning of the seller against the claim
     */
    struct Dispute {
        uint256 disputeId;
        string buyerReasoning;
        string sellerReasoning;
    }  


    ///@dev Mapping between the item ID and its Sale struct
    mapping (uint256 => Sale) public offered_items;
    ///@dev The index of the next new Sale
    uint256 public  offerIndex;
    ///@dev Mapping between the itemId the Shop's dispute struct
    mapping (uint256 => Dispute) public disputed_items;
    ///@dev The list of blacklisted seller addresses
    address[] public blacklistedSellers;
    ///@dev Faillapop vault contract
    IFP_Vault public vaultContract;
    ///@dev Faillapop DAO contract
    IFP_DAO public daoContract;


    /************************************** Events and Modifiers  *******************************************************/

    ///@notice Emitted when a user buys an item, contains the user address and the item ID
    event Buy(address user, uint item);
    ///@notice Emitted when a user creates a new sale, contains the item ID and the title of the item
    event NewItem(uint id, string title);
    ///@notice Emitted when a user modifies a sale, contains the item ID and the title of the item
    event ModifyItem(uint id, string title);
    ///@notice Emitted when a user disputes a sale, contains the user address and the item ID
    event OpenDispute(address user, uint item);
    ///@notice Emitted when a user received a refund, contains the user address and the amount
    event Reimburse(address user, uint amount);
    ///@notice Emitted when a user receives an reward NFT, contains the user address
    event AwardNFT(address user);
    ///@notice Emitted when a user is blacklisted, contains the user address
    event BlacklistSeller(address seller);

    ///@notice Check if the caller is not blacklisted
    modifier notBlacklisted() {
        require(
            !hasRole(BLACKLISTED_ROLE, msg.sender),
            "Seller is blacklisted"
        );
        _;
    }


    /************************************** External  *******************************************************/ 

    /**
        @notice Constructor of the contract
        @param dao_addr The address of the DAO contract
        @param vault_addr The address of the Vault contract
     */
    constructor(address dao_addr, address vault_addr) {
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(DAO_ROLE, dao_addr);

        daoContract = IFP_DAO(dao_addr);
        vaultContract = IFP_Vault(vault_addr);
    }


    /**
        @notice Endpoint to buy an item
        @param itemId The ID of the item being bought
        @dev The user must send the exact amount of Ether to buy the item
     */
    function doBuy(uint itemId) external payable {
        require(offered_items[itemId].seller != address(0), "itemId does not exist");
        require(offered_items[itemId].state == State.Selling, "Item cannot be bought");
        require(msg.value >= offered_items[itemId].price, "Incorrect amount of Ether sent");

        offered_items[itemId].buyer = msg.sender;
        offered_items[itemId].state = State.Pending;

        emit Buy(msg.sender, itemId);
    }


    /**
        @notice Endpoint to dispute a sale. The buyer will supply the supporting info to the DAO
        @param itemId The ID of the item being disputed
        @param buyerReasoning The reasoning of the buyer for the claim
     */
    function disputeSale(uint itemId, string calldata buyerReasoning) external {   
        require(offered_items[itemId].buyer == msg.sender, "Not the buyer");   

        offered_items[itemId].state = State.Disputed;

        // New dispute with ID = 0 until the correct one is set by the DAO
        Dispute memory newDispute = Dispute(0, buyerReasoning, "");
        disputed_items[itemId] = newDispute;
    }

    /**
        @notice Endpoint to confirm the receipt of an item and trigger the payment to the seller. 
        @param itemId The ID of the item being confirmed
     */
    function itemReceived(uint itemId) external {
        require(offered_items[itemId].buyer == msg.sender, "Not the buyer");
        offered_items[itemId].state = State.Sold;

        // Seller should be paid
        closeSale(itemId, true);
    }    


    /**
        @notice Endpoint to close a dispute. Both the DAO and the buyer could call this function to cancel a dispute
        @param itemId The ID of the item being disputed
     */
    function endDispute(uint256 itemId) external {
        require(offered_items[itemId].state == State.Disputed, "Dispute not found");

        if (msg.sender == offered_items[itemId].buyer) {
            // Self-cancelation of the dispute, the buyer accepts the item
            closeDispute(itemId);

        } else {
            // DAO resolving the dispute in favor of the seller, if the buyer wins `returnItem` will be called
            _checkRole(DAO_ROLE); // Will revert if msg.sender is doesn't have the DAO_ROLE
        }

        delete disputed_items[itemId];
        offered_items[itemId].state = State.Sold;

        // Seller should be paid
        closeSale(itemId, true);
}

    /**
        @notice Endpoint to create a new sale. The seller must have enough funds staked in the Vault so  
            price amount can be locked to desincentivice malicious behavior
        @param title The title of the item being sold
        @param description A description of the item being sold
        @param price The price in Ether of the item being sold
     */
    function newSale(uint currentId, string calldata title, string calldata description, uint256 price) external notBlacklisted() {
        require(price > 0, "Price must be greater than 0");
        require(bytes(title).length > 0, "Title cannot be empty");
        require(bytes(description).length > 0, "Description cannot be empty");


        Sale memory sale = Sale(msg.sender, address(0), title, description, price, State.Selling);
        offered_items[currentId] = sale; 

        // Lock seller staken funds to desincentivize malicious behavior
        vaultContract.doLock(msg.sender, price);

        offerIndex += 1;

        emit NewItem(currentId, title);
    }

    /**
        @notice Endpoint to modify an existing sale. Locked funds will be partially realeased if price decreases.
        @param itemId ID of the item being modified
        @param newTitle New title of the item being sold
        @param newDesc New description of the item being sold
        @param newPrice New price in Ether of the item being sold
     */
    function modifySale(uint256 itemId, string calldata newTitle, string calldata newDesc, uint256 newPrice) external {
        require(offered_items[itemId].seller == msg.sender, "Only the seller can modify the sale");   
        require(offered_items[itemId].state == State.Selling, "Sale can't be modified");	

        // Update vault
        uint priceDifference;
        if (offered_items[itemId].price > newPrice) {
            priceDifference = offered_items[itemId].price - newPrice;
            vaultContract.doUnlock(msg.sender, priceDifference);
        } else {
            priceDifference = newPrice - offered_items[itemId].price;
            vaultContract.doLock(msg.sender, priceDifference);
        }

        // Update details
        offered_items[itemId].title = newTitle;
        offered_items[itemId].description = newDesc;
        offered_items[itemId].price = newPrice;

        emit ModifyItem(itemId, newTitle);
    }


    /**
        @notice Endpoint to cancel an active sale
        @param itemId The ID of the item which sale is being cancelled
    */
    function cancelActiveSale (uint itemId) external { 
        require(offered_items[itemId].seller == msg.sender, "Only the seller can cancel the sale");   
        require(offered_items[itemId].state == State.Selling, "Sale can't be cancelled");     

        //Seller should NOT be paid
        closeSale(itemId, false);
    }    


    /**
        @notice Endpoint to set the vacation mode of a seller. If the seller is in vacation mode nobody can buy his goods
        @param _vacationMode The new vacation mode of the seller
     */
    function setVacationMode(bool _vacationMode) external {
        for (uint i = 0; i < offerIndex; i++) {
            if (offered_items[i].seller == msg.sender) {

                if (_vacationMode && offered_items[i].state == State.Selling) {
                    offered_items[i].state = State.Vacation;

                } else if (!_vacationMode && offered_items[i].state == State.Vacation) {
                    offered_items[i].state = State.Selling;

                }
            }
        }
    }


    /**
        @notice Endpoint to reply to a dispute. The seller will supply the supporting info to the DAO. If the seller does not reply,
            the admin could mark them as malicious and slash their funds
        @param itemId The ID of the item being disputed
        @param sellerReasoning The reasoning of the seller for the claim
     */
    function disputedSaleReply(uint itemId, string calldata sellerReasoning) external {    
        require(offered_items[itemId].seller == msg.sender, "Not the seller"); 
        require(offered_items[itemId].state == State.Disputed, "Item not disputed");  

        openDispute(itemId, sellerReasoning);
    }


    /** 
        @notice Endpoint to return an item, only the DAO can trigger it
        @param itemId The ID of the item being returned
     */
    function returnItem(uint256 itemId) external onlyRole(DAO_ROLE) {   
        require(offered_items[itemId].state == State.Disputed, "Item not disputed");

        /*
        * A future functionality for dealing with returns will be implemented here!
        */

        reimburse(itemId);
        closeSale(itemId, false);
    }

    /**
        @notice Endpoint to remove a malicious sale and slash the stake. The owner of the contract can remove a malicious sale and blacklist the seller
        @param itemId The ID of the item which sale is considered malicious
     */
    function removeMaliciousSale(uint itemId) external onlyRole(ADMIN_ROLE) {
        require(offered_items[itemId].seller != address(0), "itemId does not exist");

        if (offered_items[itemId].state == State.Pending) {
            reimburse(itemId);
        } else if (offered_items[itemId].state == State.Disputed) {
            reimburse(itemId);
            closeDispute(itemId);
        }

        // Seller should NOT be paid
        closeSale(itemId, false);
        blacklist(offered_items[itemId].seller); 
    }


    /************************************** Internal  *******************************************************/ 

    /**
        @notice Remove a sale from the list
        @param itemId The ID of the item which sale is being removed
        @param toBePaid If the seller should be paid or not
     */
    function closeSale(uint itemId, bool toBePaid) public {
        address seller = offered_items[itemId].seller;
        uint256 price = offered_items[itemId].price;

        // Seller payment
        if (toBePaid) {
            (bool success, ) = payable(seller).call{value: price}("");
            require(success, "Sale payment failed");
        }
        // Seller stake release
        vaultContract.doUnlock(seller, price);

        delete offered_items[itemId];
    }


    /**
        @notice Add a user to the seller blacklist and slash their funds in the Vault
        @param user The address of the seller
     */
    function blacklist(address user) internal {
        grantRole(BLACKLISTED_ROLE, user);
        //Slash the whole user stake
        vaultContract.doSlash(user);

        emit BlacklistSeller(user);
    }


    /**
        @notice Reimburst a buyer. 
        @param itemId The ID of the item being reimbursed
     */
    function reimburse(uint itemId) internal {
        uint price = offered_items[itemId].price; 	
        address buyer = offered_items[itemId].buyer;	

        // Pay the buyer back
        (bool success, ) = payable(buyer).call{value: price}("");
        require(success, "Sale payment failed");

        emit Reimburse(buyer, price);      
	}


    /** 
        @notice Open a dispute in the DAO contract
        @param itemId The ID of the item being disputed
        @param sellerReasoning The reasoning of the seller against the claim
     */
    function openDispute(uint itemId, string calldata sellerReasoning) internal {
        address buyer = offered_items[itemId].buyer;
        Dispute storage dispute = disputed_items[itemId]; 

        dispute.sellerReasoning = sellerReasoning;
        dispute.disputeId = daoContract.newDispute(
            itemId, 
            dispute.buyerReasoning, 
            dispute.sellerReasoning
        );
        // No need to "save" the above as dispute has been declared as storage

        emit OpenDispute(buyer, itemId);
    }

    /** 
        @notice Close a dispute in the DAO contract, either due to blacklisting or the buyer deciding not
            to pursue the dispute
        @param itemId The ID of the item being disputed
     */
    function closeDispute(uint itemId) internal {
        uint dId = disputed_items[itemId].disputeId;
        // Forcefully cancel an ongoing dispute
        daoContract.cancelDispute(dId);

        delete disputed_items[itemId];
    }

    /************************************** Views  *******************************************************/ 

    /**
        @notice View function to return the user's disputed sales
        @param itemId The ID of the item being disputed
        @return The dispute details
     */
	function query_dispute (uint itemId) public view returns (Dispute memory) {
		return disputed_items[itemId];
	}

    function query_sale (uint itemId) public view returns (Sale memory) {
        return offered_items[itemId];
    }

}

contracts/iebs_Faillapop_vault.sol.content:
// SPDX-License-Identifier: GPL-3.0 
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./interfaces/IFP_DAO.sol";
import "./interfaces/IFP_Shop.sol";


/** 
    @title FaillaPop vault to manage locking and staking funds for sales
    @author Faillapop team :D 
    @notice The contract allows anyone to stake and unstake Ether. When a seller publish a new item
    in the shop, the funds are locked during the sale. If the user is considered malicious
    by the DAO, the funds are slashed. 
    @dev Security review is pending... should we deploy this?
    @custom:ctf This contract is part of JC's mock-audit exercise at https://github.com/jcr-security/solidity-security-teaching-resources
*/
contract FP_Vault is AccessControl {

    /************************************** Constants *******************************************************/

    ///@dev The admin role ID for the AccessControl contract
    bytes32 constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    ///@dev The DAO role ID for the AccessControl contract
    bytes32 constant DAO_ROLE = keccak256("DAO_ROLE");
    ///@dev The Shop role ID for the AccessControl contract
    bytes32 constant SHOP_ROLE = keccak256("SHOP_ROLE");


    /************************************** State vars  *******************************************************/

    ///@dev The balance of the users in the vault
    mapping (address => uint256) public balance;
    ///@dev The amount of funds locked for selling purposes
    mapping (address => uint256) public lockedFunds;
    ///@dev address of the NFT contract
    address public nftContract;
    ///@dev Shop contract
    IFP_Shop public shopContract;
    ///@dev DAO contract
    IFP_DAO public daoContract;
    ///@dev Maximum claimable amount
    uint256 public max_claimable_amount;
    ///@dev The amount of rewards claimed by each user
    mapping (address => uint256) public rewardsClaimed;
    ///@dev The total amount of funds slashed
    uint256 public totalSlashed;


    /************************************** Events and Modifiers  *******************************************************/

    ///@dev Emitted when a user stakes funds, contains the user address and the amount staked
    event Stake(address user, uint amount);
    ///@dev Emitted when a user unstakes funds, contains the user address and the amount unstaked
    event Unstake(address user, uint amount);
    ///@dev Emitted when a user funds get locked, contains the user address and the amount locked
    event Locked(address user, uint amount);
    ///@dev Emitted when a user funds get unlocked, contains the user address and the amount unlocked
    event Unlocked(address user, uint amount);
    ///@dev Emitted when a user funds get slashed, contains the user address and the amount slashed
    event Slashed(address user, uint amount);
    ///@dev Emitted when a user claims rewards, contains the user address and the amount claimed
    event RewardsClaimed(address user, uint amount);
    ///@dev Emitted when the contract configuration is changed, contains the address of the Shop, DAO and NFT contracts
    event NewConfig(address shop, address dao, address nft);


    /** 
        @notice Check if the user has enough staked funds to lock or unstake
        @param user The address of the user to check
        @param amount The amount of funds to check
     */
    modifier enoughStaked(address user, uint amount) {
        require(balance[user] > 0, "No staked funds!");
        // Optimized version of the checks!
        unchecked {
            require(
				balance[user] - lockedFunds[user] - amount > 0, 
				"Not enough funds!"
			);
        }
        _;
    }


    /************************************** External  *******************************************************/ 

    /**
        @param token The address of the powerseller NFT contract
        @param shop The address of the Shop contract
        @param dao The address of the DAO contract
    */
    constructor(address token, address shop, address dao) {
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(DAO_ROLE, dao);
        _grantRole(SHOP_ROLE, shop);

        nftContract = token;
        shopContract = IFP_Shop(shop);
        daoContract = IFP_DAO(dao);
    }


    ///@notice Stake attached funds in the vault for later locking, the users must do it on their own
    function doStake() external payable {
        require(msg.value > 0, "Amount cannot be zero");
        balance[msg.sender] += msg.value;

        emit Stake(msg.sender, msg.value);
    }


    ///@notice Unstake unlocked funds from the vault, the user must do it on their own
    ///@param amount The amount of funds to unstake 
    function doUnstake(uint amount) external enoughStaked(msg.sender, amount) {	
        require(amount > 0, "Amount cannot be zero");

        balance[msg.sender] -= amount;

        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Unstake failed");

        emit Unstake(msg.sender, amount);
	}


    /**
        @notice Lock funds for selling purposes, the funds are locked until the sale is completed
        @param user The address of the user that is selling
        @param amount The amount of funds to lock
     */
    function doLock(address user, uint amount) external onlyRole(SHOP_ROLE) enoughStaked(user, amount) {
        require(amount > 0, "Amount cannot be zero");

        lockedFunds[user] += amount;

        emit Locked(user, amount);
    }


    ///@notice Unlock funds after the sale is completed
    function doUnlock(address user, uint256 amount) external onlyRole(SHOP_ROLE) {
        require(amount > 0, "Amount cannot be zero");
        require(amount <= lockedFunds[user], "Not enough locked funds");

        lockedFunds[user] -= amount;

        emit Unlocked(user, amount);
    }


    ///@notice Slash funds if the user is considered malicious by the DAO
    ///@param badUser The address of the malicious user to be slashed
    function doSlash(address badUser) external onlyRole(DAO_ROLE) {
        uint amount = balance[badUser];

        balance[badUser] = 0;
        lockedFunds[badUser] = 0;

        distributeSlashing(amount);

        emit Slashed(badUser, amount);
    }

    /**
        @notice Modify configuration parameters, only the owner can do it
        @param newDao The address of the new DAO contract
        @param newShop The address of the new Shop contract
        @param newNft The address of the new NFT contract
     */
    function updateConfig(address newDao, address newShop, address newNft) external onlyRole(ADMIN_ROLE) {
        daoContract = IFP_DAO(newDao);
        shopContract = IFP_Shop(newShop);
        nftContract = newNft;

        emit NewConfig(newDao, newShop, newNft);
    }


    /**
    @notice Claim rewards generated by slashing malicious users. 
        First checks if the user is elegible through the checkPrivilege function that will revert if not. 
     */
    function claimRewards() external {
        // Checks if the user is elegible
        nftContract.call( 
            abi.encodeWithSignature(
                "checkPrivilege(address)",
                msg.sender
            )
        );
        // Checks if the user has already claimed the maximum amount
        require(rewardsClaimed[msg.sender] < max_claimable_amount, "Max claimable amount reached");        

        uint256 amount = max_claimable_amount - rewardsClaimed[msg.sender];


        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Rewards payment failed");

        rewardsClaimed[msg.sender] = max_claimable_amount;

        emit RewardsClaimed(msg.sender, amount);
	}


    /************************************** Internal  *******************************************************/

    ///@notice Sets a new maximum claimable amount per user based on the total slashed amount
    function distributeSlashing(uint amount) internal {
        totalSlashed += amount;

        (, bytes memory data) = nftContract.call(abi.encodeWithSignature("totalPowersellers()"));
        uint totalPowersellers = abi.decode(data, (uint256));

        uint newMax = totalSlashed / totalPowersellers;
        max_claimable_amount = newMax;
    } 


    /************************************** Views  *******************************************************/

    ///@notice Get the balance of the vault
	function vaultBalance () public view returns (uint256) {
		return address(this).balance;
	}


    ///@notice Get the staked balance of a user
    ///@param user The address of the user to query
	function userBalance (address user) public view returns (uint256) {
		return balance[user];
	}


    ///@notice Get the locked balance of a user
    ///@param user The address of the user to query
	function userLockedBalance (address user) public view returns (uint256) {
		return lockedFunds[user];
	}    


}

contracts/interfaces/IFP_DAO.sol.content:
// SPDX-License-Identifier: GPL-3.0 

pragma solidity ^0.8.13;

/** 
    @title Iterface of the FaillaPop voting DAO for disputes
    @author Faillapop team :D 
    @notice The contract allow to vote with FPT tokens on open disputes. If the dispute is resolved in favor of the buyer,
    the seller have to refund the buyer. If the dispute is resolved in favor of the seller, the sale is closed.
    @custom:ctf This contract is of JC's mock-audit exercise at https://github.com/jcr-security/solidity-security-teaching-resources
*/
interface IFP_DAO {

    /**
        @notice Update the contract's configuration details
        @param magicWord to authenticate as privileged user
        @param newMagicWord The new password to access key features
     */
    function updateConfig(
        string calldata magicWord, 
        string calldata newMagicWord, 
        address newShop
    ) external;

    /**
        @notice Cast a vote on a dispute
        @param disputeId The ID of the target dispute
     */
    function castVote(uint disputeId) external;

    /**
        @notice Open a dispute
        @param itemId The ID of the item involved in the dispute
        @param buyerReasoning The reasoning of the buyer in favor of the claim
        @param sellerReasoning The reasoning of the seller against the claim
     */
    function newDispute( 
        uint itemId, 
        string calldata buyerReasoning, 
        string calldata sellerReasoning
    ) external returns (uint);

    /**
        @notice Resolve a finished dispute
        @param disputeId The ID of the target dispute
     */
    function endDispute(uint disputeId) external;


    /**
        @notice Cancel an ongoing dispute. Either by the buyer or blacklisting (shop contract)
        @param disputeId The ID of the target dispute
     */
    function cancelDispute(uint disputeId) external;


    /**
        @notice Award NFT to a user if they voten for the winning side
        @param disputeID The ID of the target dispute
     */
    function checkLottery(uint disputeID) external;
}

